mapper.update:
   update persona 
		set nombre=#{nombre},
		apepat=#{apepat}, 
		apemat=#{apemat},
		fechanac=#{fechanac}, 
		sexo=#{sexo},
		email=#{email},
		fijo=#{fijo},
		celular=#{celular},
		direccion=#{direccion}
		
		observaciones=#{observaciones}
		
		
			,idEspecialidad=#{idEspecialidad} 
		
		where dni=#{dni}

   update usuario 
		set estado=#{nombre},
		contrasena=#{apepat}
		where idPersona=#{idPersona}
